export function SubscriptionMarquee({ direction = 'left', speed = 'normal' }: SubscriptionMarqueeProps) {
  const speedClass = {
    slow: 'animate-marquee-slow',
    normal: 'animate-marquee',
    fast: 'animate-marquee-fast',
  }[speed]

  const animationClass = direction === 'left' ? speedClass : `${speedClass}-reverse`

  // Split services into two sets for seamless loop
  const services = SUBSCRIPTION_SERVICES

  return (
    <div className="flex overflow-hidden">
      <div
        className={`flex gap-8 ${direction === 'left' ? 'animate-marquee' : 'animate-marquee-reverse'}`}
        style={{ willChange: 'transform' }}
      >
        {/* First set */}
        {services.map((service, i) => (
          <div
            key={`${service.name}-1-${i}`}
            className="flex-shrink-0 w-14 h-14 rounded-xl flex items-center justify-center transition-transform hover:scale-110"
            style={{ backgroundColor: `${service.color}15` }}
            title={service.name}
          >
            <svg
              viewBox="0 0 24 24"
              className="w-8 h-8"
              fill={service.color}
            >
              <path d={service.path} />
            </svg>
          </div>
        ))}
        {/* Duplicate set for seamless loop */}
        {services.map((service, i) => (
          <div
            key={`${service.name}-2-${i}`}
            className="flex-shrink-0 w-14 h-14 rounded-xl flex items-center justify-center transition-transform hover:scale-110"
            style={{ backgroundColor: `${service.color}15` }}
            title={service.name}
          >
            <svg
              viewBox="0 0 24 24"
              className="w-8 h-8"
              fill={service.color}
            >
              <path d={service.path} />
            </svg>
          </div>
        ))}
      </div>
    </div>
  )
}

// Floating variant with scattered positioning
export function SubscriptionFloat({ direction = 'left' }: SubscriptionMarqueeProps) {
  const services = SUBSCRIPTION_SERVICES.slice(0, 14) // Use subset for floating

  return (
    <div className="relative h-32 overflow-hidden">
      <div
        className={`absolute flex gap-12 ${direction === 'left' ? 'animate-marquee' : 'animate-marquee-reverse'}`}
        style={{ willChange: 'transform' }}
      >
        {/* First set with varied vertical positions */}
        {services.map((service, i) => {
          const yOffset = [0, 24, 8, 32, 16, 4, 28, 12, 20, 36, 6, 30, 14, 22][i % 14]
          const size = [56, 48, 52, 44, 56, 48, 52, 48, 44, 56, 48, 52, 44, 48][i % 14]
          const opacity = [1, 0.8, 0.9, 0.7, 1, 0.85, 0.75, 0.9, 0.8, 1, 0.7, 0.85, 0.9, 0.75][i % 14]

          return (
            <div
              key={`${service.name}-float-1-${i}`}
              className="flex-shrink-0 rounded-xl flex items-center justify-center shadow-lg transition-transform hover:scale-110"
              style={{
                width: size,
                height: size,
                backgroundColor: `${service.color}20`,
                transform: `translateY(${yOffset}px)`,
                opacity,
              }}
              title={service.name}
            >
              <svg
                viewBox="0 0 24 24"
                className="w-7 h-7"
                fill={service.color}
              >
                <path d={service.path} />
              </svg>
            </div>
          )
        })}
        {/* Duplicate for seamless loop */}
        {services.map((service, i) => {
          const yOffset = [0, 24, 8, 32, 16, 4, 28, 12, 20, 36, 6, 30, 14, 22][i % 14]
          const size = [56, 48, 52, 44, 56, 48, 52, 48, 44, 56, 48, 52, 44, 48][i % 14]
          const opacity = [1, 0.8, 0.9, 0.7, 1, 0.85, 0.75, 0.9, 0.8, 1, 0.7, 0.85, 0.9, 0.75][i % 14]

          return (
            <div
              key={`${service.name}-float-2-${i}`}
              className="flex-shrink-0 rounded-xl flex items-center justify-center shadow-lg transition-transform hover:scale-110"
              style={{
                width: size,
                height: size,
                backgroundColor: `${service.color}20`,
                transform: `translateY(${yOffset}px)`,
                opacity,
              }}
              title={service.name}
            >
              <svg
                viewBox="0 0 24 24"
                className="w-7 h-7"
                fill={service.color}
              >
                <path d={service.path} />
              </svg>
            </div>
          )
        })}
      </div>
    </div>
  )
}
